$(function(){function t(){var t=Number(new Date);$.ajax({url:"../json/test.json?v="+t,data:{},"data-type":"json",type:"GET"}).done(function(t){var a=t.products,n="";$.each(a,function(t,a){a.annual_rate>=1400?n+="<tr style='background:red; color:#fff'><td><span>"+a.annual_rate_str+"</span></td><td><a href=''>"+a.duration_str+"</a></td></tr>":n+="<tr><td><span>"+a.annual_rate_str+"</span></td><td><a href=''>"+a.duration_str+"</a></td></tr>"}),$("table").html(n)}).error(function(t){$("body").html("服务器接口异常")})}t(),setInterval(t,2e3)});